import React, { useState, useEffect } from 'react';
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonImg,
  IonButton,
} from '@ionic/react';

function App() {
  const [imageUrl, setImageUrl] = useState('');

  const fetchRandomImage = () => {
    fetch('https://pic.re/image/', {
      method: 'GET', // Especifica que você está fazendo uma solicitação GET
    })
      .then((response) => {
        if (response.ok) {
          // Converte a resposta em blob (imagem)
          return response.blob();
        } else {
          console.error('Erro ao buscar a imagem da API');
        }
      })
      .then((blob) => {
        // Converte o blob em uma URL de objeto
        setImageUrl(URL.createObjectURL(blob));
      })
      .catch((error) => {
        console.error('Erro ao buscar a imagem da API:', error);
      });
  }

  useEffect(() => {
    fetchRandomImage();
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Imagem da API</IonTitle>
        </IonToolbar>
      </IonHeader>
        <IonImg src={imageUrl} />
    </IonPage>
  );
}

export default App;
