import React, { useState, useEffect } from 'react';
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonButton,
  IonImg,
} from '@ionic/react';

function App() {
  const [imageUrl, setImageUrl] = useState('');

  const fetchRandomImage = () => {
    // Defina a URL da imagem diretamente
    const imageUrl = 'https://pic.re/image';

    setImageUrl(imageUrl);
  }

  useEffect(() => {
    fetchRandomImage();
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Imagem da API</IonTitle>
        </IonToolbar>
      </IonHeader>

        <div style={{ width: '400px', height: '300px' }}>
          <IonImg src={imageUrl} />
        </div>
        <IonButton expand="full" onClick={fetchRandomImage}>
          Buscar Nova Imagem
        </IonButton>

    </IonPage>
  );
}

export default App;
