import React, { useState, useEffect } from 'react';
import { IonCard, IonCardContent, IonCardHeader, IonCardTitle, IonImg, IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonList, IonItem, IonLabel, IonSpinner } from '@ionic/react';
import axios from 'axios';

const App = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchImages() {
      try {
        const response = await axios.get('https://pic.re/image');
        setImages(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Erro ao buscar imagens da API:', error);
        setLoading(false);
      }
    }

    fetchImages();
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Imagens da API</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        {loading ? (
          <IonSpinner />
        ) : (
          <IonList>
            {images.map((image) => (
              <IonItem key={image.id}>
                <IonImg src={image.url} alt={image.description} />
                <IonLabel>
                  <h2>{image.title}</h2>
                  <p>{image.description}</p>
                </IonLabel>
              </IonItem>
            ))}
          </IonList>
        )}
      </IonContent>
    </IonPage>
  );
};

export default App;
