import React, { useState, useEffect } from 'react';
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonButton,
  IonImg,
  IonCard,
  IonCardTitle,
  IonCardSubtitle,
  IonText
} from '@ionic/react';

function App() {
  const [imageUrl, setImageUrl] = useState('');

  const fetchRandomImage = () => {
    // Defina a URL da imagem diretamente
    const imageUrl = 'https://pic.re/image';

    setImageUrl(imageUrl);
  }

  useEffect(() => {
    fetchRandomImage();
  }, []);

  return (
    <IonPage style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
      <IonHeader>

      </IonHeader>
    <IonCard style={{backgroundColor: '#DCDCDC'}}>
    <IonCardTitle style ={{fontSize: 24, fontWeight:'bold'}}>Bem-vindo ao Meu Site</IonCardTitle>    
    <br></br><IonText>Este é um parágrafo de exemplo </IonText>
         <IonButton>Clique aqui</IonButton>
         Seção Importante
         <IonImg src={imageUrl} style={{ width: '400px', height: '300px' }}></IonImg>
     </IonCard>   

    </IonPage>
  );
} <IonTitle style ={{fontSize: 24, fontWeight:'bold'}}>Bem-vindo ao Meu Site</IonTitle>    

export default App;
