import React, { useState } from 'react';
import {
  IonCard,
  IonCardTitle,
  IonText,
  IonButton,
  IonImg,
  IonPage,
  IonCardContent,
} from '@ionic/react';

function App() {
  const [imageUrl, setImageUrl] = useState('');
  const [error, setError] = useState(null);

  const generateRandomImage = () => {
    try {
      fetch('https://pic.re/image')
        .then((response) => {
          if (!response.ok) {
            throw new Error('Erro na solicitação');
          }
          return response.url;
        })
        .then((imageUrl) => {
          setImageUrl(imageUrl);
        })
        .catch((error) => {
          setError(error.message); // Define o erro no estado de erro
        });
    } catch (error) {
      setError(error.message); // Define o erro no estado de erro
    }
  };

  return (
    <IonPage style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
      <IonCard style={{ paddingBottom: 10, paddingTop: 10, backgroundColor: '#DCDCDC', width: 500, borderStyle: 'solid', borderColor: '#A9A9A9', borderTop: 0, borderLeft: 0, borderRight: 0, borderWidth: 0.2 }}>
        <IonCardTitle style={{ fontSize: 24, fontWeight: 'bold', color: '#0000CD' }}>Bem-vindo ao Meu Site</IonCardTitle>
        <br></br><br></br>
        <IonText style={{ color: 'green' }}>Este é um parágrafo de exemplo </IonText>
        <br></br><br></br>
        <IonButton color="primary">Clique aqui</IonButton>
        <br></br><br></br>
        <IonButton onClick={generateRandomImage}>Click Me</IonButton>
        <IonCardContent style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <IonImg src={imageUrl} style={{ width: '400px', height: '300px' }}></IonImg>
        </IonCardContent>
        {error && <IonText style={{ color: 'red' }}>{error}</IonText>}
      </IonCard>
    </IonPage>
  );
}

export default App;
