import React, { useState, useEffect } from 'react';
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonButton,
  IonImg,
  IonCard,
  IonCardTitle,
  IonCardSubtitle,
  IonText,
  IonMenuButton,

} from '@ionic/react';

function App() {
  const [imageUrl, setImageUrl] = useState('');

  const fetchRandomImage = () => {
    // Defina a URL da imagem diretamente
    const imageUrl = 'https://pic.re/image';

    setImageUrl(imageUrl);
  }

  useEffect(() => {
    fetchRandomImage();
  }, []);

  return (
    <IonPage style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
      <IonHeader>

      </IonHeader>

        <IonCard style={{ backgroundColor: '#DCDCDC', width:500, borderStyle:'solid', borderColor:'#A9A9A9', borderTop:0, borderLeft:0, borderRight:0, BorderWidth:0.2 }}>
        <IonCardTitle style={{ fontSize: 24, fontWeight: 'bold', color: '#0000CD' }}>Bem-vindo ao Meu Site</IonCardTitle>
        <br></br><br></br><IonText style={{color:'green'}}>Este é um parágrafo de exemplo </IonText>
        <br></br><br></br><IonButton style={{ size:'large',background: 'purple', color: 'white'}} >Clique aqui</IonButton>

      </IonCard>

      <IonCard style={{ backgroundColor: '#DCDCDC', width:500, borderStyle: 'solid', borderColor: '#A9A9A9', borderTop:0, borderLeft:0, borderRight:0, BorderWidth:0.2, }}>
      <br></br><IonText style={{fontWeight: 'bold'}}>Seção Importante</IonText>
      <br></br><br></br><IonText style={{color:'green', paddingBottom:10}}>Esta é uma seção com conteúdo relevante</IonText>
      </IonCard>  

    </IonPage>
  );
} 
export default App;

