import React, { useState, useEffect } from 'react';
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonImg,
  IonButton,
} from '@ionic/react';

const App = () => {
  const [imageUrl, setImageUrl] = useState('');

  const fetchRandomImage = () => {
    fetch('https://picsum.photos/200/300')
      .then((response) => {
        if (response.ok) {
          return response.blob();
        } else {
          console.error('Erro ao buscar a imagem da API Picsum Photos');
        }
      })
      .then((imageBlob) => {
        const imageUrl = URL.createObjectURL(imageBlob);
        setImageUrl(imageUrl);
      })
      .catch((error) => {
        console.error('Erro ao buscar a imagem da API Picsum Photos:', error);
      });
  };

  useEffect(() => {
    fetchRandomImage();
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Imagem da API Picsum Photos</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <div className="ion-padding">
          <IonImg src={imageUrl} alt="Imagem da API Picsum Photos" />
          <IonButton expand="full" onClick={fetchRandomImage}>
            Buscar Nova Imagem
          </IonButton>
        </div>
      </IonContent>
    </IonPage>
  );
};

export default App;
