import React, { useState, useEffect } from 'react';
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonImg,
  IonButton,
} from '@ionic/react';

function App() {
  const [imageUrl, setImageUrl] = useState('');
  const animeId = 20; // ID do anime "Naruto" no Jikan API (você pode substituir por outro ID)

  const fetchAnimeImage = () => {
    fetch(`https://api.jikan.moe/v3/anime/${animeId}`)
      .then((response) => response.json())
      .then((data) => {
        const imageUrl = data.image_url;
        setImageUrl(imageUrl);
      })
      .catch((error) => {
        console.error('Erro ao buscar informações do anime:', error);
      });
  };

  useEffect(() => {
    fetchAnimeImage();
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Imagem do Anime</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <div style={{ width: '400px', height: '300px' }}>
          <IonImg src={imageUrl} alt="Imagem do Anime" />
        </div>
        <IonButton expand="full" onClick={fetchAnimeImage}>
          Buscar Nova Imagem de Anime
        </IonButton>
      </IonContent>
    </IonPage>
  );
}

export default App;
