import React, { useState, useEffect } from 'react';
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonImg,
  IonButton,
} from '@ionic/react';

function App() {
  const [imageUrl, setImageUrl] = useState('');

  const fetchAnimeImage = () => {
    fetch(`https://api.jikan.moe/v3/anime/20`)
      .then((response) => response.json())
      .then((data) => {
        if (data.image_url) {
          setImageUrl(data.image_url);
        } else {
          console.error('Imagem não encontrada para este anime.');
        }
      })
      .catch((error) => {
        console.error('Erro ao buscar informações do anime:', error);
      });
  }

  useEffect(() => {
    fetchAnimeImage();
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Imagem do Anime</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <div style={{ width: '400px', height: '300px' }}>
          {imageUrl ? (
            <IonImg src={imageUrl} alt="Imagem do Anime" />
          ) : (
            <p>Imagem não encontrada</p>
          )}
        </div>
        <IonButton expand="full" onClick={fetchAnimeImage}>
          Buscar Nova Imagem de Anime
        </IonButton>
      </IonContent>
    </IonPage>
  );
}

export default App;
