import React, { useState } from 'react';
import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonButton,
  IonImg,
  IonCard,
  IonCardTitle,
  IonCardSubtitle,
  IonText,
  IonMenuButton,
  IonAlert,
} from '@ionic/react';

function App() {
  const [showAlert, setShowAlert] = useState(false);
  // const [imageUrl, setImageUrl] = useState('');

  // const fetchRandomImage = () => {
  //   Defina a URL da imagem diretamente
  //   const imageUrl = 'https://pic.re/image';

  //   setImageUrl(imageUrl);
  // }

  // useEffect(() => {
  //   fetchRandomImage();
  // }, []);

  return (
    <IonPage style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
      <IonHeader></IonHeader>

      <IonCard style={{ paddingBottom: 10, paddingTop:10, backgroundColor: '#DCDCDC', width:500, borderStyle:'solid', borderColor:'#A9A9A9', borderTop:0, borderLeft:0, borderRight:0, borderWidth:0.2 }}>
        <IonCardTitle style={{ fontSize: 24, fontWeight: 'bold', color: '#0000CD' }}>Bem-vindo ao Meu Site</IonCardTitle>
        <br></br><br></br><IonText style={{color:'green'}}>Este é um parágrafo de exemplo </IonText>
        <br></br><br></br>
        {/* Botão que abre o alerta ao ser clicado */}
        <IonButton color="primary" >Clique aqui</IonButton>
        <br></br><br></br>
        <IonButton style={{ background: 'blue', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Clique aqui</IonButton>
      </IonCard>

      <IonCard style={{ paddingBottom: 10, paddingTop:10 ,backgroundColor: '#DCDCDC', width:500, borderStyle: 'solid', borderColor: '#A9A9A9', borderTop:0, borderLeft:0, borderRight:0, borderWidth:0.2, }}>
        <IonText style={{fontWeight: 'bold'}}>Seção Importante</IonText>
        <br></br><br></br>
        <IonText style={{color:'green'}}>Esta é uma seção com conteúdo relevante</IonText>
        <br></br>
        <IonImg src='unijui.png' style={{height:250}}/>
      </IonCard>  

      {/* Componente de alerta */}
      <IonAlert
        isOpen={showAlert}
        onDidDismiss={() => setShowAlert(false)}
        header={'Título do Alerta'}
        message={'Conteúdo do Alerta.'}
        buttons={['OK']}
      />
    </IonPage>
  );
}

export default App;

