import React, { useState, useEffect } from 'react';
import { IonCard, IonCardContent, IonCardHeader, IonCardTitle, IonImg, IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonList, IonItem, IonLabel, IonSpinner } from '@ionic/react';
import axios from 'axios';

const App = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchImages() {
      try {
        const response = await axios.get('URL_DA_API');
        const contentType = response.headers['content-type'];

        if (contentType.startsWith('image/')) {
          // É uma imagem, faça o que quiser com ela
          setImages([response.data]);
        } else {
          console.error('A API não retornou uma imagem.');
        }
        setLoading(false);
      } catch (error) {
        console.error('Erro ao buscar imagens da API:', error);
        setLoading(false);
      }
    }

    fetchImages();
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Imagens da API</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        {loading ? (
          <IonSpinner />
        ) : (
          <IonList>
            {images.map((image, index) => (
              <IonItem key={index}>
                <IonImg src={`data:${image.headers['content-type']};base64,${image.data.toString('base64')}`} alt="Imagem" />
                <IonLabel>
                  <h2>Imagem {index + 1}</h2>
                  <p>Descrição da imagem {index + 1}</p>
                </IonLabel>
              </IonItem>
            ))}
          </IonList>
        )}
      </IonContent>
    </IonPage>
  );
};

export default App;
