import React, { useEffect, useState } from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonImg } from '@ionic/react';
import Axios from 'axios';

const App = () => {
  const [imageURL, setImageURL] = useState('');

  useEffect(() => {
    // Função para fazer a chamada à API
    const fetchData = async () => {
      try {
        const response = await Axios.get('https://pic.re/image'); // Substitua com a URL da sua API
        const imageData = response.data;
        const imageLink = imageData.imageURL;
        setImageURL(imageLink);
      } catch (error) {
        console.error('Erro ao buscar dados da API:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Teste</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <div className="ion-padding">
          <h2>Imagem da API:</h2>
          {imageURL ? (
            <IonImg src={imageURL} alt="Imagem da API" />
          ) : (
            <p>Carregando imagem...</p>
          )}
        </div>
      </IonContent>
    </IonPage>
  );
};

export default App;
